import { InputSettings } from './InputSettings';
import { BaseCcifError } from '@adobe/commerce-cif-common';

export class Input {
  parameters: any;
  errorOutput: BaseCcifError;
  settings: InputSettings;
  responseExtension?: object;

  constructor(inputParameters: InputBuilder) {
    const { error, settings, parameters, responseExtension } = inputParameters;
    this.errorOutput = error;
    this.settings = settings;
    this.parameters = parameters;

    if (responseExtension) {
      this.responseExtension = responseExtension;
    }
  }
}

interface InputBuilder {
  parameters: any;
  error: BaseCcifError;
  settings: InputSettings;
  responseExtension?: object;
}
