import { Coupon } from '@adobe/commerce-cif-model';
import TestEntity from '../entities/TestEntity';
import { Mapper, TranslationService } from '../../../src/mappers';
import { InputSettings } from '../../../src/io';
import { DTO } from '../../../src';

export default class TestMapper extends Mapper<TestEntity> {

  constructor(settings: InputSettings, translatorService?: TranslationService) {
    super(settings, translatorService);
  }

  mapFromInputArgsToActionParameters(mappable: any) {
    return {};
  }

  mapFromEntity(entity, mappable?: DTO): DTO {
    throw new Error('Unsupported Operation');
  }

  mapToEntity(dto: DTO): TestEntity {
    const testEntity = new TestEntity();
    testEntity.message = dto;
    return testEntity;
  }

}
