import { TranslationService } from '@diconium/commerce-cif-hybris-core';
import acceptLanguage from 'accept-language';

export class DahcTranslator implements TranslationService {
  i18nInstance: any;

  constructor(i18nInstance: any) {
    this.i18nInstance = i18nInstance;
  }

  getLocale(): string {
    return this.i18nInstance.getLocale();
  }

  getLocales(): string[] {
    return this.i18nInstance.getLocales();
  }

  setLocale(locale: string): DahcTranslator  {
    acceptLanguage.languages(this.getLocales());
    this.i18nInstance.setLocale(acceptLanguage.get(locale));
    return this;
  }

  translate(word: string): string {
    return this.i18nInstance.__(word);
  }

}
