import { expect } from 'chai';
import { dahcTranslator } from '../src/main';

describe('Translator Service (i18n)', () => {
  describe('Unit tests', () => {

    it('Constructor should create an Object.', () => {
      expect(dahcTranslator).to.be.a('Object');
    });

    it('Should allow to set a locale and then return the same locale set.', () => {
      dahcTranslator.setLocale('en');
      expect(dahcTranslator.getLocale()).to.equals('en');
    });

    it('Should return default locale if the locale set is not available.', () => {
      dahcTranslator.setLocale('pt');
      expect(dahcTranslator.getLocale()).to.equals('en');
    });

    it('Should return the translated word for the defined locale.', () => {
      expect(dahcTranslator.translate('Hello')).to.equals('Hello');
    });

    it('Should return the translated word for the defined locale. (DE)', () => {
      dahcTranslator.setLocale('de');
      expect(dahcTranslator.translate('Hello')).to.equals('Hallo');
    });

    it('Should return the translated word for the defined falllback locale. (DE)', () => {
      dahcTranslator.setLocale('de-CH');
      expect(dahcTranslator.translate('Hello')).to.equals('Hallo');
    });
  });
});
